"""
UI Assets Module
Centralizes Base64 image strings and coordinate retrieval functions
to avoid code duplication between pet_manager and pet_analyzer.
"""
from image_search import search_image

# =============================================================================
# B64 IMAGE CONSTANTS
# =============================================================================

# Pet slots (8 pets in the pet window)
B64_PET_1 = ""  # Pet 1 slot
B64_PET_2 = ""  # Pet 2 slot
B64_PET_3 = ""  # Pet 3 slot
B64_PET_4 = ""  # Pet 4 slot
B64_PET_5 = ""  # Pet 5 slot
B64_PET_6 = ""  # Pet 6 slot
B64_PET_7 = ""  # Pet 7 slot
B64_PET_8 = ""  # Pet 8 slot

B64_PETS = [B64_PET_1, B64_PET_2, B64_PET_3, B64_PET_4, B64_PET_5, B64_PET_6, B64_PET_7, B64_PET_8]

# UI Buttons
B64_PET_TAB = ""           # Pestaña de Pets en la barra de habilidades
B64_CARRY = ""             # Botón "Carry" en ventana de pet
B64_DETAILS = ""           # Botón "Details" en ventana de pet
B64_UPGRADE = ""           # Botón "Upgrade" en detalles de pet
B64_CLOSE_PET = ""         # Botón cerrar ventana de pet (X)
B64_SAVE = ""
# =============================================================================
# HELPER FUNCTIONS
# =============================================================================

def get_pet_coord(window, pet_index):
    """Obtiene coordenadas de un slot de pet (0-7) con offset +20 en X"""
    if pet_index < 0 or pet_index >= 8:
        return None
    result = search_image(B64_PETS[pet_index], window)
    if result:
        return (result[0] + 20, result[1])  # +20 en X
    print(f"[DEBUG] get_pet_coord: No se encontro PET_{pet_index + 1}")
    return None


def get_ui_coord(window, element_name):
    """Obtiene coordenadas de un elemento UI"""
    elements = {
        "PET_TAB": B64_PET_TAB,
        "CARRY": B64_CARRY,
        "DETAILS": B64_DETAILS,
        "SAVE": B64_SAVE,
        "CLOSE_PET": B64_CLOSE_PET,
        "UPGRADE": B64_UPGRADE,
    }
    b64 = elements.get(element_name)
    if b64:
        result = search_image(b64, window)
        if result:
            return result
        print(f"[DEBUG] get_ui_coord: No se encontro {element_name}")
    return None
